import { useState}  from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';
import axios from 'axios';
import React from 'react';

export default function AED() {
    let [selectedOption, setSelectedOption] = useState(0);
    let [Address,setAddress] = useState("");
    let [Name_device,setName] = useState("");
    let [IPaddress,setIP] = useState("");
    let [Port,setPort] = useState("");
    let [Editname,setEditname] = useState("");
    let [Editaddress,setEditaddress]= useState("");
    let [EditIP,setEditip]= useState("");
    let [Editport,setEditport] = useState("");
    const add = (<Tooltip id="add_tooltip">Add</Tooltip>);
    const [addshow, setaddShow] = useState(false);
    const addClose = () => setaddShow(false);
    const addShow = () => setaddShow(true);
    const edit = (<Tooltip id="edit_tooltip">Edit</Tooltip>);
    const [editshow, seteditShow] = useState(false);
    const editClose = () => {seteditShow(false);setEditname("");setEditaddress("");setEditip("");setEditport("");}
    const editShow = () => seteditShow(true);
    const del = (<Tooltip id="del_tooltip">Delete</Tooltip>);
    const [delshow, setdelShow] = useState(false);
    const delClose = () => {setdelShow(false);setEditname("");setEditaddress("");setEditip("");setEditport("");}
    const delShow = () => setdelShow(true);

    function handleChange (value) {
        setSelectedOption(value);
        setEditname(props.data[value].name);setEditip(props.data[value].ip);setEditport(props.data[value].port);setEditaddress(props.data[value].address);
      }


    function AddDevice(){
        
        axios.post('http://localhost:4000/adddevice',{
            name:Name_device,ip:IPaddress,port:Port,address:Address
          }).then(()=>{
            
          })
          .catch((error)=>{
            console.log(error);
          })
        
    }
    function EditDevice(){
        
        axios.post('http://localhost:4000/editdevice',{
            name:Editname,ip:EditIP,port:Editport,address:Editaddress,index:props.data[selectedOption]._id
          }).then(()=>{
            
          })
          .catch((error)=>{
            console.log(error)
          })
        
    }
    function DelDevice(){
        
        axios.post('http://localhost:4000/deldevice',{
            name:Editname,ip:EditIP,port:Editport,address:Editaddress,index:props.data[selectedOption]._id
          }).then(()=>{
            
          })
          .catch((error)=>{
            console.log(error)
          })
        
    }
    return (
            <>
            <OverlayTrigger placement="top" overlay={add} >
                <Button
                    variant="primary"
                    type="button"
                    size="md"
                    onClick={addShow}
                >
                    <i className="fa fa-plus">Add</i>
                </Button>
            </OverlayTrigger>
            <Modal show={addshow} onHide={addClose} size="lg" >
                    <Modal.Header closeButton>
                    <Modal.Title>Adding Device</Modal.Title>
                    </Modal.Header>
                    <Form onSubmit={AddDevice}>
                    <Modal.Body>

                            <Form.Group className="mb-3" id="Device_name" >
                                <Form.Label>Device Name</Form.Label>
                                <Form.Control required type="text" placeholder="Device1" onChange={e => setName(e.target.value)} controlId="validationCustom01" />
                            </Form.Group>
                            <Form.Group className="mb-3" id="IPaddress">
                            <Form.Label>IP Address</Form.Label>
                                <Form.Control required type="text" placeholder="127.0.0.1" onChange={e => setIP(e.target.value)} controlId="validationCustom02"/>
                            </Form.Group>
                            <Form.Group className="mb-3" id="Port">
                            <Form.Label>Port</Form.Label>
                                <Form.Control required type="text" placeholder="5000" onChange={e => setPort(e.target.value)} controlId="validationCustom02"/>
                            </Form.Group>
                            <Form.Group className="mb-3" id="Address">
                            <Form.Label>Address</Form.Label>
                                <Form.Control required type="text" placeholder="0" onChange={e => setAddress(e.target.value)} controlId="validationCustom02"/>
                            </Form.Group>
                     
                    </Modal.Body>
                    <Modal.Footer>
                    <Button type="submit" variant="primary">
                        Add
                    </Button>
                
                    <Button variant="disable" onClick={addClose}>
                        Cancel
                    </Button>
                    </Modal.Footer>
                    </Form>
                </Modal>


                <OverlayTrigger placement="top" overlay={edit} >
                <Button
                    variant="info"
                    type="button"
                    size="md"
                    onClick={editShow}
                >
                    <i className="fa fa-plus">Edit</i>
                </Button>
            </OverlayTrigger>
            <Modal show={editshow} onHide={editClose} size="lg" >
                    <Modal.Header closeButton>
                    <Modal.Title>Editting Device</Modal.Title>
                    </Modal.Header>
                    <Form onSubmit={EditDevice}>
                    <Modal.Body>

                            <Form.Group className="mb-3" id="Device_name" >
                            <Form.Label>Select your devices </Form.Label>
                            <Form.Select  id="edit"  onChange={e => handleChange(e.target.value)}>
                            <option value="999">--Select your device --</option>
                                {props.data.map((option,index) => (
                                    
                                    <option value={index}>{option.name}</option>
                                    ))}
                                </Form.Select>
          
                                <Form.Label>Device Name</Form.Label>
                                <Form.Control required type="text" value={Editname} onInput={e => setEditname(e.target.value)} controlId="validationCustom01" />
                            </Form.Group>
                            <Form.Group className="mb-3" id="IPaddress">
                            <Form.Label>IP Address</Form.Label>
                                <Form.Control required type="text" value={EditIP} onInput={e => setEditip(e.target.value)} controlId="validationCustom02"/>
                            </Form.Group>
                            <Form.Group className="mb-3" id="Port">
                            <Form.Label>Port</Form.Label>
                                <Form.Control required type="text" value={Editport} onInput={e => setEditport(e.target.value)} controlId="validationCustom02"/>
                            </Form.Group>
                            <Form.Group className="mb-3" id="Address">
                            <Form.Label>Address</Form.Label>
                                <Form.Control required type="text" value={Editaddress} onInput={e => setEditaddress(e.target.value)} controlId="validationCustom02"/>
                            </Form.Group>
                          
    
                                                   
                    </Modal.Body>
                    <Modal.Footer>
                    <Button type="submit" variant="info">
                        Edit
                    </Button>
                
                    <Button variant="disable" onClick={editClose}>
                        Cancel
                    </Button>
                    </Modal.Footer>
                    </Form>
                </Modal>

                <OverlayTrigger placement="top" overlay={del} >
                <Button
                    variant="danger"
                    type="button"
                    size="md"
                    onClick={delShow}
                >
                    <i className="fa fa-plus">Delete</i>
                </Button>
            </OverlayTrigger>
            <Modal show={delshow} onHide={delClose} size="lg" >
                    <Modal.Header closeButton>
                    <Modal.Title>Delete Device</Modal.Title>
                    </Modal.Header>
                    <Form onSubmit={DelDevice}>
                    <Modal.Body>

                            <Form.Group className="mb-3" id="Device_name" >
                            <Form.Label>Select your devices {selectedOption}</Form.Label>
                            <Form.Select  id="del"  onChange={e => handleChange(e.target.value)}>
                            <option value="999">--Select your device --</option>
                                {props.data.map((option,index) => (
                                    <option value={index}>{option.name}</option>
                                    ))}
                                </Form.Select>
                                <Form.Label>Device Name</Form.Label>
                                <Form.Control required type="text"  value={Editname} onChange={e => setEditname(e.target.value)} controlId="validationCustom01" readOnly/>
                            </Form.Group>
                            <Form.Group className="mb-3" id="IPaddress">
                            <Form.Label>IP Address</Form.Label>
                                <Form.Control required type="text"  value={EditIP} onChange={e => setEditip(e.target.value)} controlId="validationCustom02" readOnly/>
                            </Form.Group>
                            <Form.Group className="mb-3" id="Port">
                            <Form.Label>Port</Form.Label>
                                <Form.Control required type="text"  value={Editport} onChange={e => setEditport(e.target.value)} controlId="validationCustom02" readOnly/>
                            </Form.Group>
                            <Form.Group className="mb-3" id="Address">
                            <Form.Label>Address</Form.Label>
                                <Form.Control required type="text"  value={Editaddress} onChange={e => setEditaddress(e.target.value)} controlId="validationCustom02" readOnly/>
                            </Form.Group>
                         
                   
                    </Modal.Body>
                    <Modal.Footer>
                    <Button type="submit" variant="danger">
                        Delete
                    </Button>
                
                    <Button variant="disable" onClick={delClose}>
                        Cancel
                    </Button>
                    </Modal.Footer>
                    </Form>
                </Modal>
    </>
    );
  }
